DROP TABLE IF EXISTS `bt_article`;
CREATE TABLE `bt_article` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `Locale` varchar(100) DEFAULT NULL,
  `Author` int(11) DEFAULT NULL COMMENT '作者',
  `Parent` int(11) DEFAULT NULL COMMENT '上级分类编号',
  `Labels` text COMMENT '标签',
  `Pinned` bit(1) DEFAULT NULL,
  `Secret` bit(1) DEFAULT NULL,
  `Closed` bit(1) DEFAULT NULL,
  `Recomm` bit(1) DEFAULT NULL COMMENT '是否推荐文章',
  `Caption` varchar(200) DEFAULT NULL COMMENT '标题',
  `Content` longtext COMMENT '内容',
  `Grabble` longtext COMMENT '简要内容',
  `Explain` longtext,
  `Introduction` longtext,
  `Publish` timestamp NULL DEFAULT NULL,
  `Correct` text,
  `Tacback` text,
  `Address` varchar(50) DEFAULT NULL,
  `NumComm` int(11) DEFAULT NULL,
  `NumView` int(11) DEFAULT NULL,
  `AccCode` varchar(24) DEFAULT NULL,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=69 DEFAULT CHARSET=utf8;

