ion&nbsp;connection&nbsp;=&nbsp;<span class=\"keyword\">null</span><span>;&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;java.io.InputStreamReader&nbsp;in&nbsp;=&nbsp;<span class=\"keyword\">null</span><span>;&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">try</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//读取证书签名代码</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SSLContext&nbsp;sc&nbsp;=&nbsp;SSLContext.getInstance(<span class=\"string\">&quot;TLS&quot;</span><span>);&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//添加签名证书正确性验证规则（空真书则什么都不校验）</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sc.init(<span class=\"keyword\">null</span><span>,&nbsp;</span><span class=\"keyword\">new</span><span>&nbsp;TrustManager[]{</span><span class=\"keyword\">new</span><span>&nbsp;SongTrustManager()},&nbsp;</span><span class=\"keyword\">new</span><span>&nbsp;SecureRandom());&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HttpsURLConnection.setDefaultHostnameVerifier(<span class=\"keyword\">new</span><span>&nbsp;SongHostnameVerifier());&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HttpsURLConnection.setDefaultSSLSocketFactory(sc.getSocketFactory());&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//服务地址</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;url&nbsp;=&nbsp;<span class=\"keyword\">new</span><span>&nbsp;java.net.URL(</span><span class=\"string\">&quot;www.songliuchen.com&quot;</span><span>);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection&nbsp;=&nbsp;(java.net.HttpURLConnection)&nbsp;url.openConnection();&nbsp;</span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;设置是否从httpUrlConnection读入，默认情况下是true;</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setDoInput(<span class=\"keyword\">true</span><span>);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;设置是否向httpUrlConnection输出，因为这个是post请求，参数要放在</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;http正文内，因此需要设为true,&nbsp;默认情况下是false;</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setDoOutput(<span class=\"keyword\">true</span><span>);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;Post&nbsp;请求不能使用缓存</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setUseCaches(<span class=\"keyword\">false</span><span>);&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//设置请求方式</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setRequestMethod(<span class=\"string\">&quot;POST&quot;</span><span>);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//报文头信息</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setRequestProperty(<span class=\"string\">&quot;Content-Type&quot;</span><span>,&nbsp;</span><span class=\"string\">&quot;application/x-www-form-urlencoded&quot;</span><span>);&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//编码格式</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setRequestProperty(<span class=\"string\">&quot;Charset&quot;</span><span>,&nbsp;</span><span class=\"string\">&quot;utf-8&quot;</span><span>);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;设置超时时间</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.setConnectTimeout(<span class=\"number\">10000</span><span>);&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//传参数信息，根据具体情况而定</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;java.io.DataOutputStream&nbsp;dop&nbsp;=&nbsp;<span class=\"keyword\">new</span><span>&nbsp;java.io.DataOutputStream(connection.getOutputStream());&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dop.writeBytes(<span class=\"string\">&quot;username=app&amp;password=songliuchen&quot;</span><span>);&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dop.flush();&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dop.close();&nbsp;</span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//获取服务返回结果，根据情况返回结果需要做判断</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;in&nbsp;=&nbsp;<span class=\"keyword\">new</span><span>&nbsp;java.io.InputStreamReader(connection.getInputStream());&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;java.io.BufferedReader&nbsp;bufferedReader&nbsp;=&nbsp;<span class=\"keyword\">new</span><span>&nbsp;java.io.BufferedReader(in);&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;StringBuffer&nbsp;strBuffer&nbsp;=&nbsp;<span class=\"keyword\">new</span><span>&nbsp;StringBuffer();&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;line;&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">while</span><span>&nbsp;((line&nbsp;=&nbsp;bufferedReader.readLine())&nbsp;!=&nbsp;</span><span class=\"keyword\">null</span><span>)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;strBuffer.append(line);&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;result&nbsp;=&nbsp;strBuffer.toString();&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">catch</span><span>&nbsp;(Exception&nbsp;e)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;e.printStackTrace();&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">finally</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">if</span><span>&nbsp;(connection&nbsp;!=&nbsp;</span><span class=\"keyword\">null</span><span>)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;connection.disconnect();&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">if</span><span>&nbsp;(in&nbsp;!=&nbsp;</span><span class=\"keyword\">null</span><span>)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">try</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;in.close();&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;<span class=\"keyword\">catch</span><span>&nbsp;(java.io.IOException&nbsp;e)&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;e.printStackTrace();&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">return</span><span>&nbsp;result;&nbsp;</span></span></li><li><span>}&nbsp;</span></li></ol></pre><p><span style=\"font-size: medium;\"><b>三、JDK Https 空签名验证规则代码：</b></span></p><pre><ol class=\"dp-j\"><li class=\"alt\"><span><span class=\"comment\">   /**</span>&nbsp;</span></li><li><span><span class=\"comment\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;签名类</span>&nbsp;</span></li><li class=\"alt\"><span><span class=\"comment\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">private</span><span>&nbsp;</span><span class=\"keyword\">class</span><span>&nbsp;SongHostnameVerifier&nbsp;</span><span class=\"keyword\">implements</span><span>&nbsp;HostnameVerifier&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@Override</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">public</span><span>&nbsp;</span><span class=\"keyword\">boolean</span><span>&nbsp;verify(String&nbsp;hostname,&nbsp;SSLSession&nbsp;session)&nbsp;{&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;TODO&nbsp;Auto-generated&nbsp;method&nbsp;stub</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">return</span><span>&nbsp;</span><span class=\"keyword\">true</span><span>;&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">/**</span>&nbsp;</span></li><li><span><span class=\"comment\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;证书对象，实现几个方法，判断签名是否正确</span>&nbsp;</span></li><li class=\"alt\"><span><span class=\"comment\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">private</span><span>&nbsp;</span><span class=\"keyword\">class</span><span>&nbsp;SongTrustManager&nbsp;</span><span class=\"keyword\">implements</span><span>&nbsp;X509TrustManager&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@Override</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">public</span><span>&nbsp;</span><span class=\"keyword\">void</span><span>&nbsp;checkClientTrusted(X509Certificate[]&nbsp;chain,&nbsp;String&nbsp;authType)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">throws</span><span>&nbsp;CertificateException&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;TODO&nbsp;Auto-generated&nbsp;method&nbsp;stub</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li class=\"alt\"><span>&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@Override</span><span>&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">public</span><span>&nbsp;</span><span class=\"keyword\">void</span><span>&nbsp;checkServerTrusted(X509Certificate[]&nbsp;chain,&nbsp;String&nbsp;authType)&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">throws</span><span>&nbsp;CertificateException&nbsp;{&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;TODO&nbsp;Auto-generated&nbsp;method&nbsp;stub</span><span>&nbsp;</span></span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li><span>&nbsp;</span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@Override</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">public</span><span>&nbsp;X509Certificate[]&nbsp;getAcceptedIssuers()&nbsp;{&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"comment\">//&nbsp;TODO&nbsp;Auto-generated&nbsp;method&nbsp;stub</span><span>&nbsp;</span></span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">return</span><span>&nbsp;</span><span class=\"keyword\">null</span><span>;&nbsp;</span></span></li><li class=\"alt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li><li><span>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;</span></li></ol></pre><p><span style=\"font-size: larger;\"><b>下载地址:&nbsp;</b></span><a href=\"https://github.com/songliuchen/androiddemo\" style=\"background-color: rgb(231, 229, 220);\" target=\"_blank\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: larger;\">https://github.com/songliuchen/androiddemo</span></span></a></p>', ' 一、JDK Https Get 请求代码：/** * 执行httpsget请求 * 不能在主线程访问web 请求，必需单开线程 */ private void executejdkhttpsget() { new Thread() { @Override public void run() { executeJDKHttpsGet(); } }.start(); } * JDK Https Get 请求核心代码 * @return 请求结果值 */ public String executeJDKHttpsGet() { String result = null; java.net.URL url; java.net.HttpURLConnection connection = null; java.io.InputStreamReader in = null; try { //读取证书签名代码 SSLContext sc = SSLContext.getInstance(&quot;TLS&quot;); //添加签名证书正确性验证规则（空真书则什么都不校验） sc.init(null, new TrustManager[]{new SongTrustManager()}, new SecureRandom()); HttpsURLConnection.setDefaultHostnameVerifier(new SongHostnameVerifier()); HttpsURLConnection.setDefaultSSLSocketFactory(sc.getSocketFactory()); url = new java.net.URL(&quot;www.songliuchen.com&quot;); connection = (java.net.HttpURLConnection) url.openConnection(); // 设置超时时间 ', '', null, '2015-10-08 18:03:40', 'song,2015-10-08 18:31:40', '', '218.241.134.34', '0', '473', '');
INSERT INTO `bt_article` VALUES ('50', '', '1', '18', 'Fiddler,测试,安全', '\0', '\0', '\0', '\0', '安全测试(一):Fiddler服务抓包', '<p style=\"text-align: center;\"><span style=\"font-size: large;\"><span style=\"color: rgb(50, 51, 51); font-family: Times; text-align: justify; -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp;</span></span><b><span style=\"font-size: large;\">Fiddler服务抓包</span></b></p><p><span style=\"color: rgb(50, 51, 51); font-family: Times; font-size: 13.5px; text-align: justify; -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; </span><span style=\"font-size: larger;\"><span style=\"color: rgb(50, 51, 51); font-family: Times; text-align: justify; -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">Fiddler是一款由C#语言开发的免费http调试代理软件，有.net 2 和 .net 4 两种版本。Fiddler能够记录所有的你电脑和互联网之间的http通讯，Fiddler 可以也可以让你检查所有的http通讯，设置断点，以及Fiddle 所有的“进出”的数据。</span></span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; Fiddler 常用功能包括：</p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><b>&nbsp; &nbsp; &nbsp; &nbsp; </b><span style=\"font-size: medium;\"><span style=\"color: rgb(255, 0, 0);\"><b>一、获取服务访问地址及访问详细信息</b></span></span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; 运行Fiddler2，打开浏览器，访问www.songliuchen.com，此时在Fiddler中就可以看到抓取的数据了。&nbsp;</p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; text-align: center; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/hf5fqa_测试1.png\" width=\"600\" height=\"351\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; text-align: left; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; text-align: left; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"background-color: rgb(204, 255, 255);\">1）、Fiddler 中还带有一些设置，例如抓取指定格式内容，Html、CSS、JS、图片等，例如抓取指定域名的URL等</span></p><p style=\"margin-bottom: 0px; text-align: left; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; text-align: center; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/1w5eq5_测试15.png\" width=\"600\" height=\"350\" class=\"Image\" style=\"text-align: center;\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"background-color: rgb(204, 255, 255);\"> 2）、Fiddler 中可设置抓取Https协议地址，在Tools 中的Fiddler Options&nbsp; 进行设置抓取Https 协议地址。</span></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/tu8p2y_测试3.png\" width=\"558\" height=\"376\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/tiictn_测试4.png\" width=\"600\" height=\"323\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\">&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"background-color: rgb(204, 255, 255);\"> 3）、Fiddler 中可设置代理、抓取外部设备的访问地址，例如手机、Pad 、或者远程外部电脑等，同样在Tools 中的Fiddler Options中进行设置。&nbsp;</span></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/9k6e5s_测试5.png\" width=\"600\" height=\"463\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; Android手机中可在网络连接中长按连接的网络，弹出如下左图界面，选择修改网络，弹出右图界面，在其中设置代理主机地址和端口号即可（代理主机 即电脑IP地址，端口号 为在Fiddler 中设置的端口号）设置完成后通过手机访问网页即可在Fiddler中看到了</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/icfqfk_测试6.png\" width=\"200\" height=\"356\" class=\"Image\" alt=\"\" /><img src=\"http://www.songliuchen.com/Attach/month_1510/pmr9mg_测试7.png\" width=\"200\" height=\"356\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><span style=\"color: rgb(255, 0, 0);\"><b>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</b></span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><span style=\"color: rgb(255, 0, 0);\"><b>&nbsp; &nbsp; &nbsp; &nbsp; 二、模拟Get、Post请求</b></span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"background-color: rgb(204, 255, 255);\">1）、模拟Get请求</span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; text-align: center; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial; min-height: 16px;\"><b>&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/cpzfp6_测试9.png\" width=\"600\" height=\"348\" class=\"Image\" alt=\"\" /></b></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; 点击执行就相当于在浏览器中访问了该网址，在访问列表中即可看到访问记录&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/46e2px_测试10.png\" width=\"600\" height=\"323\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"background-color: rgb(204, 255, 255);\">2）、模拟Post 请求</span></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; min-height: 16px; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/phvqwc_测试11.png\" width=\"600\" height=\"347\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;点击执行就相当于在浏览器中访问了该网址，在访问列表中即可看到访问记录</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; min-height: 16px; text-align: center;\">&nbsp;&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/obhwac_测试13.png\" width=\"600\" height=\"323\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><b>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</b></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><b>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</b><span style=\"color: rgb(255, 0, 0);\"><b>三、加密解密字符串</b></span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;通过菜单中的TextWizard弹出加密、解密功能、加密方法很多，<span style=\"font-size: 13.5px; -webkit-text-stroke-width: initial;\">例如Base64、MD5、SHA等</span></p><p style=\"margin-bottom: 0px; text-align: justify; font-size: 13.5px; font-family: Times; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); -webkit-text-stroke-width: initial;\"><b>&nbsp;</b></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial; min-height: 16px; text-align: center;\"><img src=\"http://www.songliuchen.com/Attach/month_1510/phkv3i_测试14.png\" width=\"600\" height=\"375\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\"><span style=\"color: rgb(255, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 四、其他功能</span></p><p style=\"margin-bottom: 0px; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp; Fiddler 中提供的功能还是挺多的，例如Decorde，Encorde、Session验证、不同浏览器模拟、验证，测试效率等</p>', ' Fiddler服务抓包        Fiddler是一款由C#语言开发的免费http调试代理软件，有.net 2 和 .net 4 两种版本。Fiddler能够记录所有的你电脑和互联网之间的http通讯，Fiddler 可以也可以让你检查所有的http通讯，设置断点，以及Fiddle 所有的“进出”的数据。        Fiddler 常用功能包括：        一、获取服务访问地址及访问详细信息        运行Fiddler2，打开浏览器，访问www.songliuchen.com，此时在Fiddler中就可以看到抓取的数据了。              1）、Fiddler 中还带有一些设置，例如抓取指定格式内容，Html、CSS、JS、图片等，例如抓取指定域名的URL等          2）、Fiddler 中可设置抓取Https协议地址，在Tools 中的Fiddler Options  进行设置抓取Https 协议地址。          3）、Fiddler 中可设置代理、抓取外部设备的访问地址，例如手机、Pad 、或者远程外部电脑等，同样在Tools 中的Fiddler Options中进行设置。                Android手机中可在网络连接中长按连接的网络，弹出如下左图界面，选择修改网络，弹出右图界面，在其中设置代理主机地址和端口号即可（代理主机 即电脑IP地址，端口号 为在Fiddler 中设置的端口号）设置完成后通过手机访问网页即可在Fiddler中看到了              二、模拟Get、Post请求        1）、模拟Get请求              点击执行就相当于在浏览器中访问了该网址，在访问列表中即可看到访问记录                2）、模拟Post 请求               点击执行就相当于在浏览器中访问了该网址，在访问列表中即可看到访问记录               三、加密解密字符串       通过菜单中的TextWizard弹出加密、解密功能、加密方法很多，例如Base64、MD5、SHA等              四、其他功能        Fiddler 中提供的功能还是挺多的，例如Decorde，Encorde、Session验证、不同浏览器模拟、验证，测试效率等', '', null, '2015-10-09 21:38:13', 'song,2015-10-09 22:18:16', '', '42.122.54.56', '0', '1099', '');
INSERT INTO `bt_article` VALUES ('51', '', '1', '18', '', '\0', '\0', '\0', '\0', '安全测试(二):Fiddler进行电商安全测试粗谈', '<p style=\"text-align: center;\"><span style=\"font-size: large;\">Fiddler进行电商安全测试</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 在上文中已经讲述了Fiddler 中的一些功能，现在就Fiddler 的这些功能，讲讲其在网站、移动应用安全测试中的应用，特别是电商网站中的应用。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\"><b>一、用户敏感信息加密方式抓取（数据抓取）：</b></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 截取网站敏感信息，例如拦截用户登录服务，可通过的服务地址判断：</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 1）、登录、注册、找回密码、修改密码 中的参数信息username、password 等。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 2）、判断username、password 的加密方式，判断是否采用加密算法进行过加密并且加密方式是否是可解密的，针对互联网、电商网站，建议采用两种或者两种以上的加密方式，并且要求前后台采用不同的加密算法进行加密。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\"><b>二、频繁操作、会话机制验证（模拟访问）</b></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 1）、获取提交订单、修改收货地址、删除联系人等服务接口，判断后台是否做了频繁访问服务控制；判断后台是否做了频繁添加购物车、频繁提交订单、多次添加评论等控制。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 2）、验证后台是否做了会话验证机制，在用户登出系统后，是否还可以访问获取用户信息，获取用户订单信息服务接口等。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\"><b>三、商品金额、收获地址等是否做了双重验证（窜改访问）</b></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 1）、抓取提交订单服务接口后，修改订单中的支付金额信息或者修改订单中的收获地址信息后继续模拟访问服务接口、判断电商网站是否对订单金额、收获地址等做了前后台双重验证。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; 2）、获取订单信息，组织取消订单或者退换货服务接口及参数，模拟访问取消订单、或者退换货服务接口。判断电商网站是否对订单金额、收获地址等做了前后台双重验证。</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\"><b>四、其他</b></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13.5px; font-family: Times; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; Fiddler软件的核心功能就是进行网络数据的操作，网络安全测试的大部分都是围绕<span style=\"font-size: medium;\"><b>网络数据截取、模拟访问网络地址、访问网络地址篡改、访问网络地址转发</b></span>等几个方面来进行的，根据应用的不同可通过Fiddler 进行不同的测试，只要需要访问访问的情况下Fiddler 都能带来或多或少的便捷之处，只上面只是针对电商的业务做了一个最基础的Fiddler 行业使用说明，其实在电商范围内可使用Fildder进行辅助测试的功能很多，这里只是简单介绍罢了。</p>', 'Fiddler进行电商安全测试        在上文中已经讲述了Fiddler 中的一些功能，现在就Fiddler 的这些功能，讲讲其在网站、移动应用安全测试中的应用，特别是电商网站中的应用。 一、用户敏感信息加密方式抓取（数据抓取）：        截取网站敏感信息，例如拦截用户登录服务，可通过的服务地址判断：        1）、登录、注册、找回密码、修改密码 中的参数信息username、password 等。        2）、判断username、password 的加密方式，判断是否采用加密算法进行过加密并且加密方式是否是可解密的，针对互联网、电商网站，建议采用两种或者两种以上的加密方式，并且要求前后台采用不同的加密算法进行加密。 二、频繁操作、会话机制验证（模拟访问）        1）、获取提交订单、修改收货地址、删除联系人等服务接口，判断后台是否做了频繁访问服务控制；判断后台是否做了频繁添加购物车、频繁提交订单、多次添加评论等控制。        2）、验证后台是否做了会话验证机制，在用户登出系统后，是否还可以访问获取用户信息，获取用户订单信息服务接口等。 三、商品金额、收获地址等是否做了双重验证（窜改访问）        1）、抓取提交订单服务接口后，修改订单中的支付金额信息或者修改订单中的收获地址信息后继续模拟访问服务接口、判断电商网站是否对订单金额、收获地址等做了前后台双重验证。        2）、获取订单信息，组织取消订单或者退换货服务接口及参数，模拟访问取消订单、或者退换货服务接口。判断电商网站是否对订单金额、收获地址等做了前后台双重验证。 四、其他        Fiddler软件的核心功能就是进行网络数据的操作，网络安全测试的大部分都是围绕网络数据截取、模拟访问网络地址、访问网络地址篡改、访问网络地址转发等几个方面来进行的，根据应用的不同可通过Fiddler 进行不同的测试，只要需要访问访问的情况下Fiddler 都能带来或多或少的便捷之处，只上面只是针对电商的业务做了一个最基础的Fiddler 行业使用说明，其实在电商范围内可使用Fildder进行辅助测试的功能很多，这里只是简单介绍罢了。', '', null, '2015-10-12 16:56:22', 'song,2015-10-12 17:11:27', '', '123.150.90.40', '0', '708', '');
INSERT INTO `bt_article` VALUES ('52', '', '1', '15', '跨域,浏览器,Chrome', '\0', '\0', '\0', '\0', '跨域问题（三）:Chrome浏览器跨域和安全访问', '<p style=\"text-align: center;\"><span style=\"font-size: large;\">&nbsp;</span><b style=\"font-family: Helvetica; font-size: 16px; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">Chrome 浏览器跨域和安全访问问题</b><b style=\"font-family: Helvetica; font-size: 16px; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\"><br /></b></p><p><span style=\"font-family: Helvetica; font-size: 13px; -webkit-text-stroke-color: rgb(0, 0, 0); -webkit-text-stroke-width: initial;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-size: medium;\">做前端的，用Ajax获取数据，是常有的事情，同域下自然没问题了，如果是不同域获取数据，浏览器就有同源策略限制</span><br /></span></p><p>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/l2k3hj_web开发1.png\" width=\"600\" height=\"105\" class=\"Image\" alt=\"\" /></p><p style=\"text-align: center;\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-size: medium;\">Origin *&nbsp;is not allowed by Access-Control-Allow-Origin</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\"><br type=\"_moz\" /></span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; 有人会说用JSONP了。如果后台的数据接口只是返回单纯的json数据呢，而且也不能修改符合</span><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\"><span style=\"-webkit-text-stroke-color: rgb(153, 18, 0);\">JSONP的方式的数据形式</span></span></span><span style=\"font-size: medium;\">。 这个时候，我们该怎么办呢？ 如果你用的浏览器是Chrome的话，那么就有福音了。在打开</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">Chrome的地址后边加上<span style=\"line-height: normal; font-family: Monaco; color: rgb(221, 34, 68); background-color: rgb(238, 238, 238); -webkit-text-stroke-color: rgb(221, 34, 68);\">&nbsp;--args --disable-web-security --allow-file-access-from-files&nbsp;</span></span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\"><br /></span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Monaco; color: rgb(221, 34, 68); -webkit-text-stroke-color: rgb(221, 34, 68); background-color: rgb(238, 238, 238);\"><span style=\"font-size: medium;\">--args —disable-web-security 屏蔽安全访问限制</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Monaco; color: rgb(221, 34, 68); -webkit-text-stroke-color: rgb(221, 34, 68); background-color: rgb(238, 238, 238);\"><span style=\"font-size: medium;\">——allow-file-access-from-files 允许跨域文件访问</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 14px; line-height: normal; font-family: Helvetica; color: rgb(255, 38, 0); -webkit-text-stroke-color: rgb(255, 38, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 14px; line-height: normal; font-family: Helvetica; color: rgb(255, 38, 0); -webkit-text-stroke-color: rgb(255, 38, 0);\"><span style=\"font-size: medium;\">具体操作步骤如下：</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\"><b>一：windows系统（Win7)下的Chrome</b></span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; 1、关闭所有打开的Chrome。</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; 2、创建Chrome的快捷方式，修改快捷方式的目标为：</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &quot;C:\\Program Files\\Google\\Chrome\\Application\\chrome.exe&quot; --args --disable-web-security --allow-file-access-from-files</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Monaco; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); background-color: rgb(245, 245, 245); min-height: 17px;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/eu332f_web开发2.png\" width=\"473\" height=\"485\" class=\"Image\" alt=\"\" /></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Monaco; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); background-color: rgb(245, 245, 245); min-height: 17px;\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; 3、双击我们创建的Chrome快捷方式，打开Chrome，如图出现“您使用的是不受支持的命令行标记：—disable-web-security。稳定性和安全性会有所下降”，表示你取消了跨域限制了，可以随意跨域调用数据了。</span>&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 14px; line-height: normal; font-family: Tahoma; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); background-color: rgb(252, 252, 244); min-height: 17px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/i0zn6g_web开发3.png\" width=\"2546\" height=\"314\" class=\"Image\" vspace=\"0\" hspace=\"0\" border=\"0\" alt=\"\" /></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\">&nbsp;</p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Helvetica; -webkit-text-stroke-color: rgb(0, 0, 0);\"><span style=\"font-size: medium;\"><b>Mac os 下面用</b></span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 14px; line-height: normal; font-family: Tahoma; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); background-color: rgb(252, 252, 244);\"><span style=\"font-size: medium;\">&nbsp;</span></p><p style=\"margin-bottom: 0px; -webkit-text-stroke-width: initial; font-size: 13px; line-height: normal; font-family: Monaco; color: rgb(50, 51, 51); -webkit-text-stroke-color: rgb(50, 51, 51); background-color: rgb(245, 245, 245);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; open -a &quot;Google Chrome&quot; --args --disable-web-security --allow-file-access-from-files</span></p>', ' Chrome 浏览器跨域和安全访问问题       做前端的，用Ajax获取数据，是常有的事情，同域下自然没问题了，如果是不同域获取数据，浏览器就有同源策略限制             Origin * is not allowed by Access-Control-Allow-Origin        有人会说用JSONP了。如果后台的数据接口只是返回单纯的json数据呢，而且也不能修改符合JSONP的方式的数据形式。 这个时候，我们该怎么办呢？ 如果你用的浏览器是Chrome的话，那么就有福音了。在打开Chrome的地址后边加上 --args --disable-web-security --allow-file-access-from-files --args —disable-web-security 屏蔽安全访问限制——allow-file-access-from-files 允许跨域文件访问 具体操作步骤如下：一：windows系统（Win7)下的Chrome        1、关闭所有打开的Chrome。        2、创建Chrome的快捷方式，修改快捷方式的目标为：        &quot;C:\\Program Files\\Google\\Chrome\\Application\\chrome.exe&quot; --args --disable-web-security --allow-file-access-from-files                      3、双击我们创建的Chrome快捷方式，打开Chrome，如图出现“您使用的是不受支持的命令行标记：—disable-web-security。稳定性和安全性会有所下降”，表示你取消了跨域限制了，可以随意跨域调用数据了。         Mac os 下面用    open -a &quot;Google Chrome&quot; --args --disable-web-security --allow-file-access-from-files', '', null, '2015-10-14 16:44:07', 'song,2015-10-19 18:09:24', '', '123.150.90.74', '0', '1858', '');
INSERT INTO `bt_article` VALUES ('53', '', '1', '10', '', '\0', '\0', '\0', '\0', 'IOS 企业账号发布应用在线安装', '<p style=\"text-align: center;\"><span style=\"font-size: large;\">&nbsp;IOS企业应用发布</span></p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"font-size: small;\">IOS企业应用是唯一 苹果官方支持的可不通过AppStore而支持批量下载安装的方式，按照一定的协议，通过浏览器进行访问即可安装，省去了苹果审核的漫长等待过程，基于这一特性，好多公司搭起了服务器，通过提供服务的方式，支持将生成的ipa 文件上传到服务器，提供二维码扫描下载，如蒲公英等。</span></p><p style=\"text-align: left;\"><span style=\"font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; 但是这种方式只可通过企业内部测试、演示等使用，没发提供正式的在线更新功能（貌似可以自己写服务更新方式解决），废话不多少，方法摘录如下：</span></p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; <b><span style=\"font-size: medium;\">一、配置工程为Release 模式签名为发布签名， 需改PP 文件为发布许可，其中Bundle Identifier 一定要配置正确。</span></b></p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/5nzy8n_移动开发91.png\" width=\"600\" height=\"273\" class=\"Image\" alt=\"\" />&nbsp;&nbsp;</p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; <b><span style=\"font-size: medium;\">二、进行项目发布归档。</span></b></p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; 设置工程调试方式选择 IOS Device， （否则Product 菜单下面的Archive 为灰色不可用），选择Product 菜单下面的 Archive 进行归档操作，中途需要频繁输入管理员账户和密码进行全线验证操作，整个过程需要耗费一些时间，请耐心等待，归档完成会弹出如下图结果，选择导出按钮，导出ipa 文件：</p><p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://www.songliuchen.com/Attach/month_1510/qv4iy6_移动开发92.png\" width=\"600\" height=\"347\" class=\"Image\" alt=\"\" /></p><p><b><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;三、配置plist 文件</span></b></p><ol class=\"dp-xml\" style=\"list-style-position: initial; border: none; font-family: Consolas, \'Courier New\', Courier, mono, serif; white-space: pre; margin-bottom: 1px !important; margin-left: 45px !important;\"><li class=\"alt\" style=\"color: inherit; border-style: none none none solid; border-left-width: 3px; border-left-color: rgb(108, 226, 108); list-style: decimal-leading-zero outside; line-height: 14px; padding-right: 3px !important; padding-left: 10px !important;\"><span style=\"margin: 0px; padding: 0px; border: none; color: black; background-color: inherit;\"><span class=\"tag\" style=\"margin: 0px; padding: 0px; border: none; color: rgb(0, 102, 153); background-color: inherit; font-weight: bold;\">&lt;?</span><span class=\"tag-name\" style=\"margin: 0px; padding: 0px; border: none; color: rgb(0, 102, 153); background-color: inherit; font-weight: bold;\">xml</span><span style=\"margin: 0px; padding: 0px; border: none; background-color: inherit;\">&nbsp;</span><span class=\"attribute\" style=\"margin: 0px; padding: 0px; border: none; color: red; background-color: inherit;\">version</span><span style=\"margin: 0px; padding: 0px; border: none; background-color: inherit;\">=</span><span class=\"attribute-value\" style=\"margin: 0px; padding: 0px; border: none; color: blue; background-color: inherit;\">&quot;1.0&quot;</span><span style=\"margin: 0px; padding: 0px; border: none; background-color: inherit;\">&nbsp;</span><span class=\"attribute\" style=\"margin: 0px; padding: 0px; border: none; color: red; background-color: inherit;\">encoding</span><span style=\"margin: 0px; padding: 0px; border: none; background-color: inherit;\">=</span><span class=\"attribute-value\" style=\"margin: 0px; padding: 0px; border: none; color: blue; background-color: inherit;\">&quot;UTF-8&quot;</span><span class=\"tag\" style=\"margin: 0px; padding: 0px; border: none; color: rgb(0, 102, 153); background-color: inherit; font-weight: bold;\">?&gt;</span><span style=\"margin: 0px; padding: 0px; border: none; background-color: inherit;\">&nbsp;</span></span></li><li style=\"color: rgb(92, 92, 92); border-style: none none none solid; border-left-width: 3px; border-left-color: rgb(108, 226, 108); list-style: decimal-leading-zero outside; background-color: rgb(248, 248, 248); line-height: 14px; padding-right: 3px !important